import React, { useState } from "react";
import classes from "./Contact.module.css";

const ContactForm = () => {
  const [formStatus, setFormStatus] = React.useState("Send");
  const onSubmit = (e) => {
    e.preventDefault();
    setFormStatus("Submitting...");
    const { name, email, message } = e.target.elements;
    let conFom = {
      name: name.value,
      email: email.value,
      message: message.value,
    };
    console.log(conFom);
  };
  return (
    <div className="container mt-5">
      <h2 className="mb-3">React Contact Form Component Example</h2>
      <form onSubmit={onSubmit}>
        <div className="mb-3">
          <label className="form-label" htmlFor="name">
            Name
          </label>
          <input className="form-control" type="text" id="name" required />
        </div>
        <div className="mb-3">
          <label className="form-label" htmlFor="email">
            Email
          </label>
          <input className="form-control" type="email" id="email" required />
        </div>
        <div className="mb-3">
          <label className="form-label" htmlFor="message">
            Message
          </label>
          <textarea className="form-control" id="message" required />
        </div>
        <button className="btn btn-danger" type="submit">
          {formStatus}
        </button>
      </form>
    </div>
  );
};

const Contact = () => {
  return (
    <div className={classes.container} id="contact">
      <h1>Contact</h1>
      <h3>Let's Get In Touch!</h3>
      <h4 className={classes.h4}>
        Ready to start your next project with me? <br /> Give me a call or send
        me an email and I will get back
        <br /> to you as soon as possible!
        <br />
      </h4>
      <div className={classes.adiv}>
        <button>
          <a href="mailto: jogucareers@gmail.com">Send Email</a>
        </button>
      </div>
      <iframe
        src="https://www.cognitoforms.com/f/xWSnRERFcEud5UFUlnAXUQ/1"
        style={{ border: 0, width: 850 }}
        height="582"
      ></iframe>
      <script src="https://www.cognitoforms.com/f/iframe.js"></script>
      <ContactForm />
    </div>
  );
};

export default Contact;
